mapper.select:
SELECT *
FROM buyer

	 AND ID =#{id} 
	 AND C_ID =#{c_id} 
	 AND MAIN_ADDR_ID =#{main_addr_id} 
	 AND WECHAT like #{wechat} 
	 AND POINT =#{point} 
	 AND IMG =#{img} 
	 AND unix_timestamp(LAST_LOGIN_DT) >=unix_timestamp(#{last_login_dtFrom}) 
	 AND unix_timestamp(LAST_LOGIN_DT) <=unix_timestamp(#{last_login_dtTo}) 
	 AND LAST_LOGIN_DT =#{last_login_dt} 
	 AND STATUS =#{status} 
	 AND unix_timestamp(CD_DT) >=unix_timestamp(#{cd_dtFrom}) 
	 AND unix_timestamp(CD_DT) <=unix_timestamp(#{cd_dtTo}) 
	 AND CD_DT =#{cd_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND SEX =#{sex} 
	 AND BIRTH_D =#{birth_d} 
	 AND ID_CODE =#{id_code} 

	 ORDER BY   #{orderBy} 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM buyer 

	 AND ID =#{id} 
	 AND C_ID =#{c_id} 
	 AND MAIN_ADDR_ID =#{main_addr_id} 
	 AND WECHAT like #{wechat} 
	 AND POINT =#{point} 
	 AND IMG =#{img} 
	 AND unix_timestamp(LAST_LOGIN_DT) >=unix_timestamp(#{last_login_dtFrom}) 
	 AND unix_timestamp(LAST_LOGIN_DT) <=unix_timestamp(#{last_login_dtTo}) 
	 AND LAST_LOGIN_DT =#{last_login_dt} 
	 AND STATUS =#{status} 
	 AND unix_timestamp(CD_DT) >=unix_timestamp(#{cd_dtFrom}) 
	 AND unix_timestamp(CD_DT) <=unix_timestamp(#{cd_dtTo}) 
	 AND CD_DT =#{cd_dt} 
	 AND unix_timestamp(UP_DT) >=unix_timestamp(#{up_dtFrom}) 
	 AND unix_timestamp(UP_DT) <=unix_timestamp(#{up_dtTo}) 
	 AND UP_DT =#{up_dt} 
	 AND SEX =#{sex} 
	 AND BIRTH_D =#{birth_d} 
	 AND ID_CODE =#{id_code}

mapper.update:
UPDATE buyer
ID=#{id}, C_ID=#{c_id}, MAIN_ADDR_ID=#{main_addr_id}, WECHAT=#{wechat}, POINT=#{point}, IMG=#{img}, LAST_LOGIN_DT=#{last_login_dt}, STATUS=#{status}, CD_DT=#{cd_dt}, UP_DT=#{up_dt}, SEX=#{sex}, BIRTH_D=#{birth_d}, ID_CODE=#{id_code},
WHERE ID=#{id}
